tid, $mytax, array( &#039;fields&#039; =&gt; &#039;ids&#039; ) );\n$to_show = 5;  // あと5つ表示したら終わり\nwhile ( $to_show &gt; 0 ):\n  if ( empty ( $ids ) ) {\n    break;  // 投稿にタームがないので表示終わり\n  }\n\n  // 同じタームを持つ投稿(現在の投稿を除く)を取得\n  $args = array(\n    &#039;posts_per_page&#039; =&gt; $to_show,\n    &#039;post_type&#039; =&gt; $posttype,\n    &#039;meta_key&#039; =&gt; &#039;views&#039;,\n    &#039;orderby&#039; =&gt; &#039;meta_value_num&#039;,\n    &#039;tax_query&#039; =&gt; array( array(\n      &#039;taxonomy&#039; =&gt; $mytax,\n      &#039;terms&#039; =&gt; $ids,\n      &#039;include_children&#039; =&gt; false,  // タームの現階層のみ\n      // 親タームの投稿の方がランキングが高くても子タームの方が優先される\n    ) ),\n    &#039;post__not_in&#039; =&gt; array( $postid );  // 現在の投稿を除く\n  );\n  $posts = get_posts( $args );\n  foreach ( $posts as $post ):\n    setup_postdata( $post );\n    //\n    // ランキングされた投稿を表示\n    //\n  endforeach;\n  wp_reset_postdata();\n\n  $to_show -= count( $posts );  // 表示した分を減らす\n  // 先祖タームを取得\n  // 現在の投稿に2つ以上タームがある場合、最初のタームに絞って先祖を取得\n  $ancestors = get_ancestors( reset( $ids ), $mytax, &#039;taxonomy&#039; );\n  if ( count( $ancestors ) &lt; 2 ) {\n    break;  // 親タームがないので表示終わり\n  }\n  $ids = array( $ancestors[1] );  // 親タームを次の対象にする\nendwhile;\n</code></pre>\n\n\n<ul id=\"bbp-reply-revision-log-225936\" class=\"bbp-reply-revision-log\">\n\n	<li id=\"bbp-reply-revision-log-225936-item-225937\" class=\"bbp-reply-revision-log-item\">\n		この返信は16 時間、 35 分前に <a href=\"https://ja.wordpress.org/support/users/gblsm/\" title=\"gblsm のプロフィールを表示\">gblsm</a> さんが編集しました。理由: 誤記訂正\n	</li>\n\n</ul>\n\n						\n					\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"gblsm\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}}s:27:\"http://www.w3.org/2005/Atom\";a:1:{s:4:\"link\";a:1:{i:0;a